import './App.css';
import { Component } from 'react';
import uniqid from 'uniqid';

class App extends Component {
  constructor() {
    super();
    this.state = {
      steps: 0,
      id: uniqid(),
      dailyData: [],
    }
    // Bind functions to this App class
    this.handleStepsChange = this.handleStepsChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    /*this.handleHeartRateChange = this.handleHeartRateChange(this);*/
  }


  handleStepsChange(event) {
    this.setState({ 
      steps: event.target.value,
      id: this.state.id,
    });
  }

  /*handleHeartRateChange(event) {
    this.setState({ 
      heartRate: event.target.value,
    });
  }*/

  handleSubmit(event) {
    event.preventDefault();
    let day = { steps: this.state.steps, heartRate: this.state.heartRate }
    this.setState({
      steps: 0,
      id: uniqid(),
      dailyData: [ ...this.state.dailyData, day],
    });
  }

  render() {
    const {steps} = this.state;

    return (
      <div className="App">
        <h2>Your Health Data</h2>
        <form className="App-form" onSubmit={ this.handleSubmit }>

          <input className="App-input" type="number" value={steps} id="description" name="description" onChange={ this.handleStepsChange } required placeholder="Step Count"/>
          <button className="App-submit" type="submit">Add Data</button>
        </form>
          <DailyData remove={this.removeTask} tasks={tasks}/>
      </div>
    );
  }
}

export default App;

